// src/components/WidgetButton.js
import React, { useState } from 'react';
import Swap from '@dexhunterio/swaps';
import '@dexhunterio/swaps/lib/assets/style.css';

const WidgetButton = () => {
  const [showWidget, setShowWidget] = useState(false);

  return (
    <div>
      {!showWidget && (
        <div 
          className="dhs-widget-button dhs-h-14 dhs-w-14 dhs-rounded-full dhs-bg-background dhs-flex dhs-items-center dhs-justify-center dhs-cursor-pointer"
          onClick={() => setShowWidget(true)}
        >
          <img src="https://storage.googleapis.com/dexhunter-images/public/hunt-token.svg" className="dhs-w-full dhs-h-full" alt="DexHunter" />
        </div>
      )}
      {showWidget && (
        <Swap 
          orderTypes={["SWAP", "LIMIT"]}
          colors={{
            background: "#0E0F12",
            containers: "#191B23",
            subText: "#88919E",
            mainText: "#FFFFFF",
            buttonText: "#FFFFFF",
            accent: "#007DFF"
          }}
          theme="dark"
          width={450}
          partnerCode="fluxpointstudios,inc.6164647231713973366d3964387965646663663533796871356a357a73673073353877707a616d77657872787066656c677a3277676b3073396c396671633933747963387a75347a37687039646c736b61326b65773974726467386e73636a637133736b357333da39a3ee5e6b4b0d3255bfef95601890afd80709"
          partnerName="FluxPointStudios,Inc."
        />
      )}
    </div>
  );
};

export default WidgetButton;
