// src/App.js
import React, { useState } from 'react';
import WidgetButton from './components/widgetButton.js';
import './App.css';

const App = () => {
  const [message, setMessage] = useState('');
  const [clientId, setClientId] = useState(null);
  const [accessToken, setAccessToken] = useState(localStorage.getItem('accessToken'));
  const [discordId, setDiscordId] = useState(localStorage.getItem('discordId'));

  // Fetch configuration from the server
  const fetchConfig = async () => {
    try {
      const response = await fetch('/config');
      const data = await response.json();
      setClientId(data.clientId);
      console.log('Fetched config:', data);
    } catch (error) {
      console.error('Error fetching config:', error);
    }
  };

  // Login with Discord
  const loginWithDiscord = async () => {
    if (!clientId) {
      await fetchConfig();
    }

    const redirectUri = encodeURIComponent('https://fancy-pond-7060.iagon.io');
    const scope = encodeURIComponent('identify applications.entitlements');
    const responseType = 'code';
    const authUrl = `https://discord.com/oauth2/authorize?client_id=${clientId}&redirect_uri=${redirectUri}&response_type=${responseType}&scope=${scope}`;
    console.log('Redirecting to Discord auth URL:', authUrl);
    window.location.href = authUrl;
  };

  // Handle callback from Discord OAuth
  const handleCallback = async () => {
    const urlParams = new URLSearchParams(window.location.search);
    const code = urlParams.get('code');
    if (code) {
      console.log('Authorization Code:', code);
      const tokenResponse = await fetch('/discord/token', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ code }),
      });
      const tokenData = await tokenResponse.json();
      console.log('Token Data:', tokenData);
      const token = tokenData.access_token;

      localStorage.setItem('accessToken', token);
      setAccessToken(token);

      const userResponse = await fetch('https://discord.com/api/users/@me', {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });
      const userData = await userResponse.json();
      console.log('User Data:', userData);

      if (WHITELISTED_USER_IDS.includes(userData.id)) {
        setDiscordId(userData.id);
        localStorage.setItem('discordId', userData.id);
        console.log(`User ${userData.id} is whitelisted.`);
      } else {
        const entitlementsResponse = await fetch(`/discord/entitlements?accessToken=${token}`);
        const entitlements = await entitlementsResponse.json();
        console.log('Entitlements:', entitlements);

        if (entitlements.some(entitlement => !entitlement.deleted && (!entitlement.ends_at || new Date(entitlement.ends_at) > new Date()))) {
          setDiscordId(entitlements[0].user_id);
          localStorage.setItem('discordId', entitlements[0].user_id);
          console.log('Saved discordId to localStorage:', entitlements[0].user_id);
        } else {
          alert('This command requires a premium subscription! Upgrade now to get access to these features!');
          window.location.href = 'https://discord.com/application-directory/1254680166664507413/store';
        }
      }
    }
  };

  // Send message function
  const sendMessage = async () => {
    console.log('Send message button clicked.');
    if (!message) {
      console.log('Message is empty, not sending.');
      return;
    }

    const chatBox = document.getElementById('chat-box');
    chatBox.innerHTML += `<div class="chat-message user">${message}</div>`;
    document.getElementById('message').value = '';
    document.getElementById('message').focus();
    console.log('Message field cleared and focused.');

    const typingIndicator = document.createElement('div');
    typingIndicator.className = 'chat-message bot';
    typingIndicator.textContent = 'Thinking...';
    typingIndicator.id = 'typingIndicator';
    chatBox.appendChild(typingIndicator);
    chatBox.scrollTop = chatBox.scrollHeight;
    console.log('Added typing indicator.');

    try {
      const response = await fetch('https://afe2am4o4cexr5ccxicifawnga0aqxqc.lambda-url.us-east-1.on.aws/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ message }),
      });
      const data = await response.json();
      console.log('Bot response received:', data.message);

      if (typingIndicator && typingIndicator.parentNode === chatBox) {
        chatBox.removeChild(typingIndicator);
        console.log('Removed typing indicator.');
      }

      chatBox.innerHTML += `<div class="chat-message bot">${data.message}</div>`;
      chatBox.scrollTop = chatBox.scrollHeight;

      await fetch('/save-chat', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ discordId, chatHistory: chatBox.innerHTML }),
      });
      console.log('Chat history saved for discordId:', discordId);
    } catch (error) {
      console.error('Error sending message:', error);
    }
  };


  
  window.onload = handleCallback;

  return (
    <div className="container">
      <img id="logo" src="https://i.ibb.co/rsvpknc/FPS-Logo-Color-only.png" alt="Your Logo" />
      <h1>cDPT</h1>
      <h3>The Only AI Assistant You Will Ever Need For Cardano</h3>
      <button className="button" id="login-button" onClick={loginWithDiscord}>
        <img src="https://i.ibb.co/89Qrp66/discord-mark-white.png" alt="Discord Logo" width="20" height="20" />
        Login with Discord
      </button>
      <div id="chat-container" className="hidden">
        <div className="chat-box" id="chat-box"></div>
        <div className="input-group">
          <label htmlFor="message">Message</label>
          <textarea id="message" placeholder="Enter your message here..." value={message} onChange={(e) => setMessage(e.target.value)}></textarea>
        </div>
        <div className="button-group">
          <button className="button" onClick={confirmClearChat}>Clear Chat</button>
          <button className="button" onClick={sendMessage}>Send Message</button>
          <button className="button" onClick={() => loadPreviousChat('669286a6143a8475482c3e05')}>Load Previous Chat</button>
          <button className="button" onClick={getCurrentEpochDetails}>Get Current Epoch Details</button>
          <button className="button" onClick={() => getMarketPrice('ADA')}>ADA/IAG Price</button>
        </div>
      </div>
      <WidgetButton />
    </div>
  );
};

export default App;
